function changeLanguage(lang) {
  localStorage.setItem('lang', lang); 
  location.reload();

  document.querySelectorAll('[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    el.innerHTML = translations[lang][key] || key;
  });

}

window.onload = () => {
  const savedLang = localStorage.getItem('lang') || 'vi';
  document.querySelectorAll('[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    el.innerHTML = translations[savedLang][key] || key;
  });

};



function updatePlaceholders(lang) {
  const t = translations[lang];
  if (!t) return;

  document.getElementById("name").placeholder = t.name;
  document.getElementById("email").placeholder = t.email;
  document.getElementById("subject").placeholder = t.subject;
  document.getElementById("message").placeholder = t.message;
  document.getElementById("submitBtn").value = translations[lang].submit;

  document.querySelectorAll('[data-i18n]').forEach(el => {
    const key = el.getAttribute('data-i18n');
    el.textContent = t[key] || key;
  });
}

window.addEventListener('DOMContentLoaded', () => {
  const lang = localStorage.getItem('lang') || 'vi';
  updatePlaceholders(lang);
});

